mapper.update:
 update orders set

	order_status=#{orderStatus, jdbcType=SMALLINT}


	,records=#{records, jdbcType=VARCHAR}


	,last_update_time=#{lastUpdateTime, jdbcType=VARCHAR}

where
	order_id=#{orderId, jdbcType=VARCHAR}
and
    create_user=#{createUser, jdbcType=VARCHAR}

 update orders set

	idcard_front=#{idCardFront, jdbcType=VARCHAR}


	,idcard_back=#{idCardBack, jdbcType=VARCHAR}

where
	order_id=#{orderId, jdbcType=VARCHAR}
and 
	create_user=#{createUser, jdbcType=VARCHAR}

 update orders set

	deliveryNo=#{deliveryNo, jdbcType=VARCHAR}


	order_status=#{orderStatus, jdbcType=SMALLINT}


	,records=#{records, jdbcType=VARCHAR}


	,last_update_time=#{lastUpdateTime, jdbcType=VARCHAR}

where
	order_id=#{orderId, jdbcType=VARCHAR}

mapper.select:
select * from orders WHERE order_status =#{status,jdbcType=SMALLINT}
and (name like "%${search}%" or order_id like "%${search}%" or phone like "%${search}%" or idcard like "%${search}%" or hospital like "%${search}%" or doctor like "%${search}%" )
order by create_time DESC limit ${offset},${limit}
